package co.edu.eci.blueprints.dto.response;

import co.edu.eci.blueprints.model.Blueprint;
import co.edu.eci.blueprints.model.Point;

import java.util.List;

public record BlueprintResponse(
        String author,
        String name,
        List<Point> points,
        int pointsCount
) {
    public static BlueprintResponse fromBlueprint(Blueprint blueprint) {
        return new BlueprintResponse(
                blueprint.getAuthor(),
                blueprint.getName(),
                blueprint.getPoints(),
                blueprint.getPoints() != null ? blueprint.getPoints().size() : 0
        );
    }
}

package co.edu.eci.blueprints.dto.response;

import co.edu.eci.blueprints.model.Blueprint;
import co.edu.eci.blueprints.model.Point;

import java.util.List;

public record BlueprintResponse(
        Long id,
        String author,
        String name,
        List<Point> points,
        int pointsCount
) {
    public static BlueprintResponse fromBlueprint(Blueprint blueprint) {
        return new BlueprintResponse(
                blueprint.getId(),
                blueprint.getAuthor(),
                blueprint.getName(),
                blueprint.getPoints(),
                blueprint.getPoints() != null ? blueprint.getPoints().size() : 0
        );
    }
}

package co.edu.eci.blueprints.model;

import java.util.*;

public class Blueprint {
    private String author;
    private String name;
    private List<Point> points = new ArrayList<>();

    public Blueprint() {}

    public Blueprint(String author, String name, List<Point> pts) {
        this.author = author;
        this.name = name;
        if (pts != null) points.addAll(pts);
    }

    public String getAuthor() { return author; }
    public void setAuthor(String author) { this.author = author; }
    public String getName() { return name; }
    public void setName(String name) { this.name = name; }
    public List<Point> getPoints() { return points; }
    public void setPoints(List<Point> points) { this.points = points; }
    public void addPoint(Point p) { points.add(p); }

    @Override
    public boolean equals(Object o) {
        if (this == o) return true;
        if (!(o instanceof Blueprint bp)) return false;
        return Objects.equals(author, bp.author) && Objects.equals(name, bp.name);
    }

    @Override
    public int hashCode() { return Objects.hash(author, name); }
}

package co.edu.eci.blueprints.model;

import jakarta.persistence.*;
import java.util.*;

@Entity
public class Blueprint {

    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Long id;

    private String author;
    private String name;

    @OneToMany(cascade = CascadeType.ALL, orphanRemoval = true)
    private List<Point> points = new ArrayList<>();

    public Blueprint() {}

    public Blueprint(String author, String name, List<Point> pts) {
        this.author = author;
        this.name = name;
        if (pts != null) points.addAll(pts);
    }

    public Long getId() { return id; }
    public String getAuthor() { return author; }
    public void setAuthor(String author) { this.author = author; }
    public String getName() { return name; }
    public void setName(String name) { this.name = name; }
    public List<Point> getPoints() { return points; }
    public void setPoints(List<Point> points) { this.points = points; }
    public void addPoint(Point p) { points.add(p); }

    @Override
    public boolean equals(Object o) {
        if (this == o) return true;
        if (!(o instanceof Blueprint bp)) return false;
        return Objects.equals(author, bp.author) && Objects.equals(name, bp.name);
    }

    @Override
    public int hashCode() { return Objects.hash(author, name); }
}

package co.edu.eci.blueprints.model;

public class Point {
	private int x;
	private int y;

	public Point() {}

	public Point(int x, int y) {
		this.x = x;
		this.y = y;
	}

	public int getX() { return x; }
	public void setX(int x) { this.x = x; }
	public int getY() { return y; }
	public void setY(int y) { this.y = y; }
}

package co.edu.eci.blueprints.model;

import jakarta.persistence.*;

@Entity
public class Point {

    @Id
    @GeneratedValue(strategy = GenerationType.IDENTITY)
    private Long id;

    private int x;
    private int y;

    public Point() {}

    public Point(int x, int y) {
        this.x = x;
        this.y = y;
    }

    public Long getId() { return id; }
    public int getX() { return x; }
    public void setX(int x) { this.x = x; }
    public int getY() { return y; }
    public void setY(int y) { this.y = y; }
}
